con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder("GetProfilesRequest#Response")

//// get all the prices through an XPATH
def array = holder.getNodeValues( "//*:GetProfilesResponse/*:Profiles[*]/@token")
def token  = array.flatten().findAll{ it != null }
def uniqueNumbers = token.clone().unique().size()

assert uniqueNumbers == token.size() : log.warn("duplicate token values!!!")
